import {
  RiImage2Line,
  RiEmotionHappyLine,
  RiSendPlaneLine,
} from "react-icons/ri";
import { BiMicrophone, BiMap } from "react-icons/bi";

import io from "socket.io-client";

const socket = io("https://chat-production-f865.up.railway.app");

import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useEffect } from "react";

const IMG2 =
  "data:image/jpeg;base64,";

const IMG1 =
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ6sGddmMZFZCqb7aJFx5eV-8FGj2gJWz7abGntj8IuyYdAv7W2HEJyi5WY3xbpLLzf-Zg&usqp=CAU";

const SendMessage = ({ setChats, setUserMessage }) => {
  const schema = yup
    .object({
      message: yup.string().required(),
    })
    .required("Ups! Necesitamos que llenes los campos");

  const { register, handleSubmit, reset } = useForm({
    resolver: yupResolver(schema),
  });

  const setMessage = (message, main) => {
    setChats((prev) => [
      ...prev,
      {
        name: main ? localStorage.getItem("user") : "EMA",
        src: main ? IMG1 : IMG2,
        messages: [message],
        main: main,
      },
    ]);
  };

  const onSubmit = ({ message }) => {
    setMessage(message, true);
    setUserMessage(message);
    socket.emit("chat:mensaje", {
      message,
      user: localStorage.getItem("user"),
    });

    reset();
  };

  return (
    <div className=" w-full bg-white p-4 dark:bg-[#22222A]">
      <form className="relative" onSubmit={handleSubmit(onSubmit)}>
        <BiMicrophone className="absolute left-4 top-[10px] text-xl hover:cursor-pointer dark:text-gray-300" />
        <input
          type="text"
          className="w-full rounded-full bg-gray-100 py-2 pl-12 pr-28 outline-none dark:bg-[#1E1F24] dark:text-gray-300"
          defaultValue=""
          onChange={(e) => socket.emit("chat:escribiendo", e.target.value)}
          {...register("message", { required: true })}
        />
        {/* Icons message */}
        <div className="absolute right-4 top-3 flex items-center gap-2 dark:text-gray-300">
          <RiImage2Line className="hover:cursor-pointer" />
          <RiEmotionHappyLine className="hover:cursor-pointer" />
          <RiSendPlaneLine className="hover:cursor-pointer" />
          <BiMap className="hover:cursor-pointer" />
        </div>
      </form>
    </div>
  );
};

export default SendMessage;
